mapper.select:
select tib.ID,tib.BATCH_NO,tib.PRODUCTION_DATE,tib.EFFECTIVE_DATE,tib.MTR_ID,tib.MTR_NAME,tib.IN_COUNT,tib.IN_UNIT_NAME,tib.IN_UNIT_RATE,tib.ORDER_PRICE,tib.IN_TOTLA_PRICE
from tb_import_detail ti,tb_import_mtr_batch tib
where ti.MTR_ID=tib.MTR_ID and tib.MTR_ID=${mtrId} group by tib.BATCH_NO ;

select count(bd.bom_id) as quty from mtr_data m,bom_detail bd
where  m.id=bd.mtr_id and bd.semi_finished='0' and m.id=${mtrId}
GROUP BY m.id;

select count(bd.bom_id) as quty from prd_data p,bom_detail bd
where  p.id=bd.mtr_id and bd.semi_finished='1' and p.id=${prdId}
GROUP BY p.id;

select ${returnFiled} from ${tableName} where
	${returnFiled} LIKE concat('%',#{likeDateStr},'%')
order by id desc limit 1;
