mapper.select:
		select * from tb_outport_detail


				MTR_ID = #{mtrId}


				OUTPORT_ID = #{outportId}




                order by ${sidx} ${order}


                order by ID desc



			limit #{offset}, #{limit}

SELECT od.* FROM tb_outport_info oi, tb_outport_detail od WHERE oi.id = od.OUTPORT_ID
AND oi.ORDER_ID = #{orderId}
AND od.MTR_ID = #{mtrId}

SELECT DISTINCT ib.IN_UNIT_NAME as mtrUnit, ib.IN_UNIT_RATE as mtrRate, ib.ORDER_PRICE as price, (ib.ORDER_PRICE*od.OUT_COUNT) as outPrice, od.*, od.BATCH_NO as batchNo, od.OUT_DATE as outDate, od.OUT_COUNT as outCount FROM tb_outport_info oi, tb_outport_detail od, tb_import_mtr_batch ib, mtr_data m WHERE oi.id = od.OUTPORT_ID AND od.MTR_ID = m.id AND od.MTR_ID = ib.MTR_ID AND od.BATCH_NO = ib.BATCH_NO
AND oi.ID = #{outportId}
AND oi.ORDER_ID = #{orderId}
AND od.MTR_ID = #{mtrId}

SELECT
	p.PRD_CODE,
	p.PRD_NAME,
	( SELECT t.TYPE_NAME FROM tb_type_info t WHERE t.id = p.TYPE_ID ) AS prdTypeName,
	bd.mtr_id AS mtrId,
	if(bd.semi_finished='0',(select m.MTR_CODE from mtr_data m where m.id=bd.mtr_id),(select m.PRD_CODE from prd_data m where m.id=bd.mtr_id)) as mtrCode,
	if(bd.semi_finished='0',(select m.MTR_NAME from mtr_data m where m.id=bd.mtr_id),(select m.PRD_NAME from prd_data m where m.id=bd.mtr_id)) as mtrName,
	if(bd.semi_finished='0',(select t.TYPE_NAME from mtr_data m,tb_type_info t where m.id=bd.mtr_id and t.id = m.TYPE_ID),(select t.TYPE_NAME from prd_data m,tb_type_info t where m.id=bd.mtr_id and t.id = m.TYPE_ID)) as mtrTypeName,
	if(bd.semi_finished='0',(select m.TAKE_STN_ID from mtr_data m where m.id=bd.mtr_id),(select m.PDC_STN from prd_data m where m.id=bd.mtr_id)) as TAKE_STN_ID,
	if(bd.semi_finished='0',(select t.TYPE_NAME from mtr_data m,tb_type_info t where m.id=bd.mtr_id and t.id = m.TAKE_STN_ID),(select t.TYPE_NAME from prd_data m,tb_type_info t where m.id=bd.mtr_id and t.id = m.PDC_STN)) as takeStnName,
	if(bd.semi_finished='0',(select m.WAREHOUSE_ID from mtr_data m where m.id=bd.mtr_id),(NULL)) as WAREHOUSE_ID,
	if(bd.semi_finished='0',(select t.TYPE_NAME from mtr_data m,tb_type_info t where m.id=bd.mtr_id and t.id = m.WAREHOUSE_ID),(NULL)) as wareHouseName,
	bd.gross_wgt AS mtrGrossWgt,
	bd.modi_wgt AS mtrModiWgt,
	bd.net_wgt AS netWgt,
	if(bd.semi_finished='0',(select t.TYPE_NAME from tb_type_info t,mtr_data m where m.id=bd.mtr_id and t.id=m.MINI_UNIT),(select t.TYPE_NAME from tb_type_info t, prd_data m where m.id=bd.mtr_id and t.id=m.UNIT)) as formulaUnit,
	if(bd.semi_finished='0',(select t.TYPE_NAME from tb_type_info t,mtr_data m where m.id=bd.mtr_id and t.id=m.MINI_UNIT),(NULL)) as miniUnitName,
	if(bd.semi_finished='0',(select m.MINI_RATE from mtr_data m where m.id=bd.mtr_id),(select m.PRD_RATE from prd_data m where m.id=bd.mtr_id)) as miniRate,
	( SELECT t.TYPE_NAME FROM tb_type_info t WHERE t.id = bd.mtr_cut_id ) AS mtrCut,
	bd.semi_finished AS semiFinished,
	if(bd.semi_finished='0',(0),(select m.POT_WEIGHT from prd_data m where m.id=bd.mtr_id)) as potWeight
	FROM
	bom_info bi,
	bom_detail bd,
	prd_data p
	WHERE
	bi.prd_id = p.id
	AND bi.id = bd.bom_id
	AND p.id = ${prdId}
ORDER BY WAREHOUSE_ID ASC
